// SPDX-License-Identifier: MIT
pragma solidity 0.6.6;
pragma experimental ABIEncoderV2;

import './SLA.sol';
import './SLORegistry.sol';
import './Staking.sol';
import './interfaces/IStakeRegistry.sol';
import './interfaces/IPeriodRegistry.sol';
import './interfaces/IMessengerRegistry.sol';
import '@openzeppelin/contracts/token/ERC20/ERC20.sol';

/**
 * @title Details
 * @dev Details is a contract to fetch details of contracts with a single RPC endpoint
 */
contract Details {
    struct TokenStake {
        address tokenAddress;
        uint256 totalStake;
        uint256 usersPool;
        uint256 providerPool;
    }

    struct DtokenDetails {
        address tokenAddress;
        uint256 totalSupply;
        address dTokenAddress;
        string dTokenSymbol;
        string dTokenName;
        uint256 balance;
        uint256 allowance;
    }

    /**
     * @dev external view function that returns all dynamic agreement information
     * @return stakersCount 2. amount of stakers
     * @return nextVerifiablePeriod 3. amount of stakers
     */

    function getSLADynamicDetails(address _slaAddress)
        external
        view
        returns (
            uint256 stakersCount,
            uint256 nextVerifiablePeriod,
            uint64 leverage
        )
    {
        SLA sla = SLA(_slaAddress);
        stakersCount = sla.getStakersLength();
        nextVerifiablePeriod = sla.nextVerifiablePeriod();
        leverage = sla.leverage();
    }

    function getSLAStaticDetails(address _slaAddress, SLORegistry _sloRegistry)
        external
        view
        returns (
            address slaOwner,
            address messengerAddress,
            uint256 sloValue,
            uint256 creationBlockNumber,
            uint256 slaId,
            uint128 initialPeriodId,
            uint128 finalPeriodId,
            bool whiteListed,
            IPeriodRegistry.PeriodType periodType,
            SLORegistry.SLOType sloType,
            string memory ipfsHash
        )
    {
        SLA sla = SLA(_slaAddress);
        slaOwner = sla.owner();
        messengerAddress = sla.messengerAddress();
        whiteListed = sla.whitelistedContract();
        periodType = sla.periodType();
        (sloValue, sloType) = _sloRegistry.registeredSLO(_slaAddress);
        creationBlockNumber = sla.creationBlockNumber();
        slaId = sla.slaID();
        ipfsHash = sla.ipfsHash();
        initialPeriodId = sla.initialPeriodId();
        finalPeriodId = sla.finalPeriodId();
    }

    function getSLADetailsArrays(address _slaAddress)
        external
        view
        returns (
            SLA.PeriodSLI[] memory periodSLIs,
            TokenStake[] memory tokensStake
        )
    {
        SLA sla = SLA(_slaAddress);
        uint256 initialPeriodId = sla.initialPeriodId();
        uint256 finalPeriodId = sla.finalPeriodId();
        uint256 periodIdsLength = finalPeriodId - initialPeriodId + 1;
        periodSLIs = new SLA.PeriodSLI[](periodIdsLength);
        for (uint256 index = 0; index < periodIdsLength; index++) {
            uint256 periodId = initialPeriodId + index;
            (uint256 timestamp, uint256 sli, SLA.Status status) = sla
            .periodSLIs(periodId);
            periodSLIs[index] = SLA.PeriodSLI({
                status: status,
                sli: sli,
                timestamp: timestamp
            });
        }
        uint256 allowedTokensLength = sla.getAllowedTokensLength();
        tokensStake = new TokenStake[](allowedTokensLength);
        for (uint256 index = 0; index < allowedTokensLength; index++) {
            address tokenAddress = sla.allowedTokens(index);
            tokensStake[index] = TokenStake({
                tokenAddress: tokenAddress,
                totalStake: sla.usersPool(sla.allowedTokens(index)) +
                    sla.providerPool(sla.allowedTokens(index)),
                usersPool: sla.usersPool(sla.allowedTokens(index)),
                providerPool: sla.providerPool(sla.allowedTokens(index))
            });
        }
    }

    function getDTokensDetails(address _slaAddress, address _owner)
        public
        view
        returns (
            DtokenDetails[] memory dpTokens,
            DtokenDetails[] memory duTokens
        )
    {
        bool fromOwner = _owner != address(0x0);
        SLA sla = SLA(_slaAddress);
        uint256 allowedTokensLength = sla.getAllowedTokensLength();
        dpTokens = new DtokenDetails[](allowedTokensLength);
        duTokens = new DtokenDetails[](allowedTokensLength);
        for (uint256 index = 0; index < allowedTokensLength; index++) {
            address tokenAddress = sla.allowedTokens(index);
            address dpTokenAddress = address(sla.dpTokenRegistry(tokenAddress));
            dpTokens[index] = DtokenDetails({
                dTokenAddress: dpTokenAddress,
                tokenAddress: tokenAddress,
                totalSupply: ERC20(dpTokenAddress).totalSupply(),
                dTokenSymbol: ERC20(dpTokenAddress).symbol(),
                dTokenName: ERC20(dpTokenAddress).name(),
                balance: fromOwner
                    ? ERC20(dpTokenAddress).balanceOf(_owner)
                    : 0,
                allowance: fromOwner
                    ? ERC20(dpTokenAddress).allowance(_owner, _slaAddress)
                    : 0
            });
            address duTokenAddress = address(sla.duTokenRegistry(tokenAddress));
            duTokens[index] = DtokenDetails({
                dTokenAddress: duTokenAddress,
                tokenAddress: tokenAddress,
                totalSupply: ERC20(duTokenAddress).totalSupply(),
                dTokenSymbol: ERC20(duTokenAddress).symbol(),
                dTokenName: ERC20(duTokenAddress).name(),
                balance: fromOwner
                    ? ERC20(duTokenAddress).balanceOf(_owner)
                    : 0,
                allowance: fromOwner
                    ? ERC20(duTokenAddress).allowance(_owner, _slaAddress)
                    : 0
            });
        }
    }
}

contracts/SLA.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.6.6;
pragma experimental ABIEncoderV2;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/math/SafeMath.sol';
import './interfaces/ISLARegistry.sol';
import './interfaces/IStakeRegistry.sol';
import './interfaces/IPeriodRegistry.sol';
import './SLORegistry.sol';
import './Staking.sol';

contract SLA is Staking {
    using SafeMath for uint256;

    enum Status {
        NotVerified,
        Respected,
        NotRespected
    }

    struct PeriodSLI {
        uint256 timestamp;
        uint256 sli;
        Status status;
    }

    //
    string public ipfsHash;
    address public immutable messengerAddress;
    ISLARegistry private _slaRegistry;
    IPeriodRegistry private immutable _periodRegistry;
    SLORegistry private immutable _sloRegistry;
    uint256 public immutable creationBlockNumber;
    uint128 public immutable initialPeriodId;
    uint128 public immutable finalPeriodId;
    IPeriodRegistry.PeriodType public immutable periodType;
    /// @dev extra data for customized workflows
    bytes32[] public extraData;

    bool public userWithdrawLocked = true;
    uint256 public nextVerifiablePeriod;

    /// @dev periodId=>PeriodSLI mapping
    mapping(uint256 => PeriodSLI) public periodSLIs;

    event SLICreated(uint256 timestamp, uint256 sli, uint256 periodId);

    event Stake(
        address indexed tokenAddress,
        uint256 indexed periodId,
        address indexed caller,
        uint256 amount,
        string position
    );
    event ProviderWithdraw(
        address indexed tokenAddress,
        uint256 indexed periodId,
        address indexed caller,
        uint256 amount
    );
    event UserWithdraw(
        address indexed tokenAddress,
        uint256 indexed periodId,
        address indexed caller,
        uint256 amount
    );

    modifier onlyMessenger() {
        require(msg.sender == messengerAddress, 'not messenger');
        _;
    }

    modifier onlyISLARegistry() {
        require(msg.sender == address(_slaRegistry), 'not ISLARegistry');
        _;
    }

    constructor(
        address _owner,
        bool _whitelisted,
        IPeriodRegistry.PeriodType _periodType,
        address _messengerAddress,
        uint128 _initialPeriodId,
        uint128 _finalPeriodId,
        uint128 _slaID,
        string memory _ipfsHash,
        bytes32[] memory _extraData,
        uint64 _leverage
    )
        public
        Staking(
            ISLARegistry(msg.sender),
            _whitelisted,
            _slaID,
            _leverage,
            _owner
        )
    {
        transferOwnership(_owner);
        ipfsHash = _ipfsHash;
        messengerAddress = _messengerAddress;
        _slaRegistry = ISLARegistry(msg.sender);
        _periodRegistry = IPeriodRegistry(_slaRegistry.periodRegistry());
        _sloRegistry = SLORegistry(_slaRegistry.sloRegistry());
        creationBlockNumber = block.number;
        initialPeriodId = _initialPeriodId;
        finalPeriodId = _finalPeriodId;
        periodType = _periodType;
        extraData = _extraData;
        nextVerifiablePeriod = _initialPeriodId;
    }

    function registerSLI(uint256 _sli, uint256 _periodId)
        external
        onlyMessenger
    {
        uint256 sliValue = _sli;
        uint256 precision = 10000;

        emit SLICreated(block.timestamp, sliValue, _periodId);
        nextVerifiablePeriod = _periodId + 1;
        PeriodSLI storage periodSLI = periodSLIs[_periodId];
        periodSLI.sli = sliValue;
        periodSLI.timestamp = block.timestamp;

        uint256 deviation = _sloRegistry.getDeviation(
            sliValue,
            address(this),
            precision
        );

        uint256 normalizedPeriodId = _periodId.sub(initialPeriodId).add(1);

        uint256 rewardPercentage = deviation.mul(normalizedPeriodId).div(
            finalPeriodId - initialPeriodId + 1
        );

        if (_sloRegistry.isRespected(sliValue, address(this))) {
            periodSLI.status = Status.Respected;

            _setRespectedPeriodReward(_periodId, rewardPercentage, precision);
        } else {
            periodSLI.status = Status.NotRespected;

            _setUsersCompensation(_periodId, rewardPercentage, precision);
        }
    }

    function isAllowedPeriod(uint256 _periodId) external view returns (bool) {
        if (_periodId < initialPeriodId) return false;
        if (_periodId > finalPeriodId) return false;
        return true;
    }

    function contractFinished() public view returns (bool) {
        (, uint256 endOfLastValidPeriod) = _periodRegistry.getPeriodStartAndEnd(
            periodType,
            finalPeriodId
        );
        return (block.timestamp >= endOfLastValidPeriod &&
            periodSLIs[finalPeriodId].status != Status.NotVerified);
    }

    function stakeTokens(
        uint256 _amount,
        address _token,
        string calldata _position
    ) external {
        require(_amount > 0, "zero staking not allowed.");
        string memory position = _position;
        bool isContractFinished = contractFinished();
        require(!isContractFinished, 'finished contract');
        _stake(_amount, _token, position);
        emit Stake(_token, nextVerifiablePeriod, msg.sender, _amount, position);
        IStakeRegistry stakeRegistry = IStakeRegistry(
            _slaRegistry.stakeRegistry()
        );
        stakeRegistry.registerStakedSla(msg.sender);
    }

    function withdrawProviderTokens(uint256 _amount, address _tokenAddress)
        external
    {
        bool isContractFinished = contractFinished();
        require(isContractFinished, 'not finished');

        emit ProviderWithdraw(
            _tokenAddress,
            nextVerifiablePeriod,
            msg.sender,
            _amount
        );
        _withdrawProviderTokens(_amount, _tokenAddress);
    }

    function withdrawUserTokens(uint256 _amount, address _tokenAddress)
        external
    {
        bool isContractFinished = contractFinished();
        require(isContractFinished, 'not finished');

        emit UserWithdraw(
            _tokenAddress,
            nextVerifiablePeriod,
            msg.sender,
            _amount
        );
        _withdrawUserTokens(_amount, _tokenAddress);
    }

    function getStakersLength() external view returns (uint256) {
        return stakers.length;
    }
}

contracts/SLORegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.6.6;
pragma experimental ABIEncoderV2;

import '@openzeppelin/contracts/math/SafeMath.sol';

/**
 * @title SLORegistry
 * @dev SLORegistry is a contract for handling creation of service level
 * objectives and querying those service level objectives
 */
contract SLORegistry {
    using SafeMath for uint256;
    using SafeMath for int256;

    enum SLOType {
        EqualTo,
        NotEqualTo,
        SmallerThan,
        SmallerOrEqualTo,
        GreaterThan,
        GreaterOrEqualTo
    }

    struct SLO {
        uint256 sloValue;
        SLOType sloType;
    }
    /**
     * @dev SLO Registered event
     * @param sla 1. -
     * @param sloValue 2. -
     * @param sloType 3. -
     */
    event SLORegistered(address indexed sla, uint256 sloValue, SLOType sloType);

    address private slaRegistry;
    mapping(address => SLO) public registeredSLO;

    modifier onlySLARegistry() {
        require(
            msg.sender == slaRegistry,
            'Should only be called using the SLARegistry contract'
        );
        _;
    }

    function setSLARegistry() public {
        // Only able to trigger this function once
        require(
            address(slaRegistry) == address(0),
            'SLARegistry address has already been set'
        );
        slaRegistry = msg.sender;
    }

    /**
     * @dev public function for creating service level objectives
     * @param _sloValue 1. -
     * @param _sloType 2. -
     * @param _slaAddress 3. -
     */
    function registerSLO(
        uint256 _sloValue,
        SLOType _sloType,
        address _slaAddress
    ) public onlySLARegistry {
        registeredSLO[_slaAddress] = SLO({
            sloValue: _sloValue,
            sloType: _sloType
        });
        emit SLORegistered(_slaAddress, _sloValue, _sloType);
    }

    /**
     * @dev external view function to check a value against the SLO
     * @param _value The SLI value to check against the SLO
     * @return boolean with the SLO honoured state
     */
    function isRespected(uint256 _value, address _slaAddress)
        public
        view
        returns (bool)
    {
        SLO memory slo = registeredSLO[_slaAddress];
        SLOType sloType = slo.sloType;
        uint256 sloValue = slo.sloValue;

        if (sloType == SLOType.EqualTo) {
            return _value == sloValue;
        }

        if (sloType == SLOType.NotEqualTo) {
            return _value != sloValue;
        }

        if (sloType == SLOType.SmallerThan) {
            return _value < sloValue;
        }

        if (sloType == SLOType.SmallerOrEqualTo) {
            return _value <= sloValue;
        }

        if (sloType == SLOType.GreaterThan) {
            return _value > sloValue;
        }

        if (sloType == SLOType.GreaterOrEqualTo) {
            return _value >= sloValue;
        }

        revert("isRespected wasn't executed properly");
    }

    /**
     * @dev external view function to get the percentage difference between SLI and SLO
     * @param _sli The SLI value to check against the SLO
     * @param _slaAddress The SLO value to check against the SLI
     * @param _precision The precision for the calculation
     * @return uint256 with the deviation value for the selected sli and sla
     */
    function getDeviation(
        uint256 _sli,
        address _slaAddress,
        uint256 _precision
    ) public view returns (uint256) {
        uint256 sliValue = _sli;
        SLO memory slo = registeredSLO[_slaAddress];
        SLOType sloType = slo.sloType;
        uint256 sloValue = slo.sloValue;
        uint256 precision = _precision;

        // Ensures a positive deviation for greater / small comparisions
        // The deviation is the percentage difference between SLI and SLO
        uint256 deviation = (
            _sli >= sloValue ? _sli.sub(sloValue) : sloValue.sub(_sli)
        ).mul(precision).div(sliValue.add(sloValue).div(2));

        // Enforces a deviation capped at 25%
        if (deviation > precision.div(100).mul(25)) {
            deviation = precision.div(100).mul(25);
        }

        if (sloType == SLOType.EqualTo) {
            // Fixed deviation for this comparison, the reward percentage fully driven by verification period
            deviation = precision.div(100).mul(1);
            return deviation;
        }

        if (sloType == SLOType.NotEqualTo) {
            // Fixed deviation for this comparison, the reward percentage fully driven by verification period
            deviation = precision.div(100).mul(1);
            return deviation;
        }

        if (sloType == SLOType.SmallerThan) {
            return deviation;
        }

        if (sloType == SLOType.SmallerOrEqualTo) {
            return deviation;
        }

        if (sloType == SLOType.GreaterThan) {
            return deviation;
        }

        if (sloType == SLOType.GreaterOrEqualTo) {
            return deviation;
        }

        revert("getDeviation wasn't executed properly");
    }
}

contracts/Staking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.6.6;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import '@openzeppelin/contracts/presets/ERC20PresetMinterPauser.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/math/SafeMath.sol';
import './interfaces/IStakeRegistry.sol';
import './interfaces/ISLARegistry.sol';
import './interfaces/IPeriodRegistry.sol';
import './StringUtils.sol';

contract Staking is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for ERC20;

    /// @dev StakeRegistry contract
    IStakeRegistry private _stakeRegistry;
    /// @dev SLARegistry contract
    IPeriodRegistry private immutable _periodRegistry;
    /// @dev DSLA token address to burn fees
    address private immutable _dslaTokenAddress;

    /// @dev current SLA id
    uint128 public immutable slaID;

    /// @dev (tokenAddress=>uint256) total pooled token balance
    mapping(address => uint256) public providerPool;
    /// @dev (tokenAddress=>uint256) total pooled token balance
    mapping(address => uint256) public usersPool;

    ///@dev (tokenAddress=>dTokenAddress) to keep track of dToken for users
    mapping(address => ERC20PresetMinterPauser) public duTokenRegistry;
    ///@dev (tokenAddress=>dTokenAddress) to keep track of dToken for provider
    mapping(address => ERC20PresetMinterPauser) public dpTokenRegistry;

    /// @dev address[] of the stakers of the SLA contract
    address[] public stakers;
    /// @dev (slaOwner=>bool)
    mapping(address => bool) public registeredStakers;
    /// @dev array with the allowed tokens addresses for the current SLA
    address[] public allowedTokens;

    /// @dev corresponds to the burn rate of DSLA tokens, but divided by 1000 i.e burn percentage = burnRate/1000 %
    uint256 public immutable DSLAburnRate;

    /// @dev boolean to declare if contract is whitelisted
    bool public immutable whitelistedContract;
    /// @dev (userAddress=bool) to declare whitelisted addresses
    mapping(address => bool) public whitelist;

    uint64 public immutable leverage;

    modifier onlyAllowedToken(address _token) {
        require(isAllowedToken(_token) == true, 'token not allowed');
        _;
    }

    modifier onlyAllowedPosition(string memory _position) {
        require(
            keccak256(abi.encodePacked(_position)) ==
                keccak256(abi.encodePacked('long')) ||
                keccak256(abi.encodePacked(_position)) ==
                keccak256(abi.encodePacked('short')),
            'position not allowed'
        );
        _;
    }

    modifier onlyWhitelisted() {
        if (whitelistedContract == true) {
            require(whitelist[msg.sender] == true, 'not whitelisted');
        }
        _;
    }

    event ProviderRewardGenerated(
        uint256 indexed periodId,
        address indexed tokenAddress,
        uint256 rewardPercentage,
        uint256 rewardPercentagePrecision,
        uint256 rewardAmount
    );

    event UserCompensationGenerated(
        uint256 indexed periodId,
        address indexed tokenAddress,
        uint256 usersStake,
        uint256 leverage,
        uint256 compensation
    );

    event DTokensCreated(
        address indexed tokenAddress,
        address indexed dpTokenAddress,
        string dpTokenName,
        string dpTokenSymbol,
        address indexed duTokenAddress,
        string duTokenName,
        string duTokenSymbol
    );

    constructor(
        ISLARegistry slaRegistry_,
        bool whitelistedContract_,
        uint128 slaID_,
        uint64 leverage_,
        address contractOwner_
    ) public {
        _stakeRegistry = IStakeRegistry(slaRegistry_.stakeRegistry());
        _periodRegistry = IPeriodRegistry(slaRegistry_.periodRegistry());
        whitelistedContract = whitelistedContract_;
        (
            uint256 _DSLAburnRate,
            ,
            ,
            ,
            ,
            ,
            ,
            uint64 _maxLeverage,

        ) = _stakeRegistry.getStakingParameters();
        _dslaTokenAddress = _stakeRegistry.DSLATokenAddress();
        DSLAburnRate = _DSLAburnRate;
        whitelist[contractOwner_] = true;
        slaID = slaID_;
        require(
            leverage_ <= _maxLeverage && leverage_ >= 1,
            'incorrect leverage'
        );
        leverage = leverage_;
    }

    function addUsersToWhitelist(address[] memory _userAddresses)
        public
        onlyOwner
    {
        for (uint256 index = 0; index < _userAddresses.length; index++) {
            if (whitelist[_userAddresses[index]] == false) {
                whitelist[_userAddresses[index]] = true;
            }
        }
    }

    function removeUsersFromWhitelist(address[] calldata _userAddresses)
        external
        onlyOwner
    {
        for (uint256 index = 0; index < _userAddresses.length; index++) {
            if (whitelist[_userAddresses[index]] == true) {
                whitelist[_userAddresses[index]] = false;
            }
        }
    }

    function addAllowedTokens(address _tokenAddress) external onlyOwner {
        (, , , , , , uint256 maxTokenLength, , ) = _stakeRegistry
            .getStakingParameters();
        require(!isAllowedToken(_tokenAddress), 'already added');
        require(_stakeRegistry.isAllowedToken(_tokenAddress), 'not allowed');
        allowedTokens.push(_tokenAddress);
        require(maxTokenLength >= allowedTokens.length, 'max token length');
        string memory dTokenID = StringUtils.uintToStr(slaID);
        string memory duTokenName = string(
            abi.encodePacked('DSLA-KO-', dTokenID)
        );
        string memory duTokenSymbol = string(
            abi.encodePacked('DSLA-SP-', dTokenID)
        );
        string memory dpTokenName = string(
            abi.encodePacked('DSLA-OK-', dTokenID)
        );
        string memory dpTokenSymbol = string(
            abi.encodePacked('DSLA-LP-', dTokenID)
        );

        ERC20PresetMinterPauser duToken = ERC20PresetMinterPauser(
            _stakeRegistry.createDToken(duTokenName, duTokenSymbol)
        );
        ERC20PresetMinterPauser dpToken = ERC20PresetMinterPauser(
            _stakeRegistry.createDToken(dpTokenName, dpTokenSymbol)
        );

        dpTokenRegistry[_tokenAddress] = dpToken;
        duTokenRegistry[_tokenAddress] = duToken;
        emit DTokensCreated(
            _tokenAddress,
            address(dpToken),
            dpTokenName,
            dpTokenName,
            address(duToken),
            duTokenName,
            duTokenName
        );
    }

    function _stake(
        uint256 _amount,
        address _tokenAddress,
        string memory _position
    )
        internal
        onlyAllowedToken(_tokenAddress)
        onlyAllowedPosition(_position)
        onlyWhitelisted
    {
        ERC20(_tokenAddress).safeTransferFrom(
            msg.sender,
            address(this),
            _amount
        );

        // DSLA-SP proofs of SLA Position
        // string memory short = 'short';
        if (
            keccak256(abi.encodePacked(_position)) ==
            keccak256(abi.encodePacked('short'))
        ) {
            (uint256 providerStake, uint256 usersStake) = (
                providerPool[_tokenAddress],
                usersPool[_tokenAddress]
            );
            require(
                usersStake.add(_amount).mul(leverage) <= providerStake,
                'user stake'
            );
            ERC20PresetMinterPauser duToken = duTokenRegistry[_tokenAddress];
            uint256 p0 = duToken.totalSupply();

            // If there are no minted tokens, then mint them 1:1
            if (p0 == 0) {
                duToken.mint(msg.sender, _amount);
            } else {
                uint256 t0 = usersPool[_tokenAddress];
                // mint dTokens proportionally
                uint256 mintedDUTokens = _amount.mul(p0).div(t0);
                duToken.mint(msg.sender, mintedDUTokens);
            }
            usersPool[_tokenAddress] = usersPool[_tokenAddress].add(_amount);
        }

        // DSLA-LP proofs of SLA Position
        // string memory long = 'long';
        if (
            keccak256(abi.encodePacked(_position)) ==
            keccak256(abi.encodePacked('long'))
        ) {
            ERC20PresetMinterPauser dpToken = dpTokenRegistry[_tokenAddress];
            uint256 p0 = dpToken.totalSupply();

            if (p0 == 0) {
                dpToken.mint(msg.sender, _amount);
            } else {
                uint256 t0 = providerPool[_tokenAddress];
                // mint dTokens proportionally
                uint256 mintedDPTokens = _amount.mul(p0).div(t0);
                dpToken.mint(msg.sender, mintedDPTokens);
            }

            providerPool[_tokenAddress] = providerPool[_tokenAddress].add(
                _amount
            );
        }

        if (registeredStakers[msg.sender] == false) {
            registeredStakers[msg.sender] = true;
            stakers.push(msg.sender);
        }
    }

    function _setRespectedPeriodReward(
        uint256 _periodId,
        uint256 _rewardPercentage,
        uint256 _precision
    ) internal {
        for (uint256 index = 0; index < allowedTokens.length; index++) {
            address tokenAddress = allowedTokens[index];
            uint256 usersStake = usersPool[tokenAddress];
            uint256 reward = usersStake.mul(_rewardPercentage).div(_precision);

            usersPool[tokenAddress] = usersPool[tokenAddress].sub(reward);

            providerPool[tokenAddress] = providerPool[tokenAddress].add(reward);

            emit ProviderRewardGenerated(
                _periodId,
                tokenAddress,
                _rewardPercentage,
                _precision,
                reward
            );
        }
    }

    function _setUsersCompensation(
        uint256 _periodId,
        uint256 _rewardPercentage,
        uint256 _precision
    ) internal {
        for (uint256 index = 0; index < allowedTokens.length; index++) {
            address tokenAddress = allowedTokens[index];
            uint256 usersStake = usersPool[tokenAddress];

            uint256 compensation = usersStake
                .mul(leverage)
                .mul(_rewardPercentage)
                .div(_precision);

            providerPool[tokenAddress] = providerPool[tokenAddress].sub(
                compensation
            );

            usersPool[tokenAddress] = usersPool[tokenAddress].add(compensation);

            emit UserCompensationGenerated(
                _periodId,
                tokenAddress,
                usersStake,
                leverage,
                compensation
            );
        }
    }

    function _withdrawProviderTokens(uint256 _amount, address _tokenAddress)
        internal
        onlyAllowedToken(_tokenAddress)
    {
        ERC20PresetMinterPauser dpToken = dpTokenRegistry[_tokenAddress];
        uint256 p0 = dpToken.totalSupply();
        uint256 t0 = providerPool[_tokenAddress];
        // Burn duTokens in a way that doesn't affect the Provider Pool / DSLA-SP Pool average
        // t0/p0 = (t0-_amount)/(p0-burnedDPTokens)
        uint256 burnedDPTokens = _amount.mul(p0).div(t0);
        dpToken.burnFrom(msg.sender, burnedDPTokens);
        providerPool[_tokenAddress] = providerPool[_tokenAddress].sub(_amount);
        ERC20(_tokenAddress).safeTransfer(msg.sender, _amount);
    }

    function _withdrawUserTokens(uint256 _amount, address _tokenAddress)
        internal
        onlyAllowedToken(_tokenAddress)
    {
        ERC20PresetMinterPauser duToken = duTokenRegistry[_tokenAddress];
        uint256 p0 = duToken.totalSupply();
        uint256 t0 = usersPool[_tokenAddress];
        // Burn duTokens in a way that doesn't affect the User Pool / DSLA-SP Pool average
        // t0/p0 = (t0-_amount)/(p0-burnedDUTokens)
        uint256 burnedDUTokens = _amount.mul(p0).div(t0);
        duToken.burnFrom(msg.sender, burnedDUTokens);
        usersPool[_tokenAddress] = usersPool[_tokenAddress].sub(_amount);
        ERC20(_tokenAddress).safeTransfer(msg.sender, _amount);
    }

    function getAllowedTokensLength() external view returns (uint256) {
        return allowedTokens.length;
    }

    function isAllowedToken(address _tokenAddress) public view returns (bool) {
        for (uint256 index = 0; index < allowedTokens.length; index++) {
            if (allowedTokens[index] == _tokenAddress) {
                return true;
            }
        }
        return false;
    }
}

